<template>
  <div class="zlp-action">
    <ul>
      <slot></slot>
      <zlp-dropdown>
        <ul>
          <div class="zlp-action-item" @mouseover="mouseover" @click="click">
            <slot name="dropdown"></slot>
          </div>
        </ul>
      </zlp-dropdown>
      <!-- <div class="zlp-action-more" @mouseover="mouseover" @mouseout="mouseout">
        <div style="height: 50px; line-height: 37px">...</div>
        <div class="zlp-action-dropdown" :class="activeClassName">
          <ul>
            <div class="zlp-action-item" @mouseover="mouseover" @click="click">
              <slot name="dropdown"></slot>
            </div>
          </ul>
        </div>
      </div> -->
    </ul>
  </div>
</template>
<script>
export default {
  name: "zlp-action",
  data() {
    return {
      active: false,
    };
  },
  methods: {
    mouseover() {
      this.active = true;
    },
    mouseout() {
      this.active = false;
    },
    click() {
      this.active = false;
    },
  },
  computed: {
    activeClassName() {
      return this.active ? "zlp-action-dropdown-active" : "";
    },
  },
};
</script>
