<template>
  <div class="zlp-button">
    <button
      :style="style"
      :class="btnClassName"
      @click="click"
      :disabled="disabled"
    >
      <i v-if="icon" :class="iconClassName"></i>
      <slot></slot>
    </button>
  </div>
</template>
<script>
export default {
  name: "zlp-button",
  props: {
    size: {
      type: String,
    },
    icon: {
      type: String,
    },
    disable: {
      type: Boolean,
      default: false,
    },
    radius: {
      type: Boolean,
      default: false,
    },
    type: {
      type: String,
      default: "default",
    },
    color: {
      type: String,
    },
    bgColor: {
      type: String,
    },
  },
  data() {
    return {};
  },
  computed: {
    iconClassName() {
      if (this.icon) {
        return this.icon;
      }
    },
    disabled() {
      return this.disable == true ? true : false;
    },
    style() {
      let styleName = "";
      if (this.size) {
        let sizeValue = 14;
        let widthValue = 100;
        let heightValue = 38;
        switch (this.size) {
          case "large":
            sizeValue = 16;
            widthValue = 120;
            heightValue = 46;
            break;
          case "small":
            sizeValue = 10;
            widthValue = 50;
            heightValue = 30;
            break;
          case "mini":
            sizeValue = 12;
            widthValue = 30;
            heightValue = 22;
            break;
          default:
            sizeValue = 14;
            widthValue = 100;
            heightValue = 50;
            break;
        }
        let fontSize = "font-size:" + sizeValue + "px;";
        let width = "width:" + widthValue + "px;";
        let height = "height:" + heightValue + "px;";
        styleName = styleName + fontSize  + height;
      }
      if (this.color) {
        let color = "color:" + this.color + ";";
        styleName = styleName + color;
      }
      if (this.bgColor) {
        let bgColor = "background-color:" + this.bgColor + ";";
        styleName = styleName + bgColor;
      }
      if (this.radius) {
        let radius = "border-radius:10px;";
        styleName = styleName + radius;
      }
      return styleName;
    },
    btnClassName() {
      let className = "";
      switch (this.type) {
        case "default":
          className = "zlp-button-default";
          break;
        case "light":
          className = "zlp-button-light";
          break;
        case "dark":
          className = "zlp-button-dark";
          break;
        case "running":
          className = "zlp-button-running";
          break;
        case "success":
          className = "zlp-button-success";
          break;
        case "wait":
          className = "zlp-button-wait";
          break;
        case "warning":
          className = "zlp-button-warning";
          break;
        case "fail":
          className = "zlp-button-fail";
          break;
        case "stop":
          className = "zlp-button-stop";
          break;
        case "warm":
          className = "zlp-button-warm";
          break;
        case "skyblue":
          className = "zlp-button-skyblue";
          break;
        case "chinared":
          className = "zlp-button-chinared";
          break;
        case "green":
          className = "zlp-button-green";
          break;
        case "add":
          className = "zlp-button-add";
          break;
        case "edit":
          className = "zlp-button-edit";
          break;
        case "delete":
          className = "zlp-button-delete";
          break;
        case "view":
          className = "zlp-button-view";
          break;

        case "text":
          className = "zlp-button-text";
          break;
        case "link":
          className = "zlp-button-link";
          break;
        default:
          break;
      }
      return className;
    },
  },
  methods: {
    click(e) {
      this.$emit("click", e);
    },
  },
};
</script>
